import { motion } from 'framer-motion';
import { useState, useEffect } from 'react';

export default function Home() {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isMoving, setIsMoving] = useState(false);
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    let timeout;
    const handleMouseMove = (event) => {
      setMousePosition({ x: event.clientX, y: event.clientY });
      setIsMoving(true);
      clearTimeout(timeout);
      timeout = setTimeout(() => setIsMoving(false), 300);
    };
    window.addEventListener("mousemove", handleMouseMove);
    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
      clearTimeout(timeout);
    };
  }, []);

  useEffect(() => {
    const particleArray = Array.from({ length: 200 }).map(() => ({
      x: Math.random() * window.innerWidth,
      y: Math.random() * window.innerHeight,
      size: Math.random() * 3 + 1,
    }));
    setParticles(particleArray);
  }, []);

  return (
    <div className="relative min-h-screen bg-black text-white flex items-center justify-center overflow-hidden">
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_center,_rgba(18,18,18,1)_0%,_rgba(255,69,0,0.1)_30%,_rgba(0,0,0,1)_100%)]" />
      
      {particles.map((particle, index) => (
        <motion.div
          key={index}
          className="absolute bg-white rounded-full"
          style={{
            width: particle.size,
            height: particle.size,
            top: particle.y,
            left: particle.x,
            opacity: 0.3,
          }}
          animate={{
            x: mousePosition.x / 50 - window.innerWidth / 100,
            y: mousePosition.y / 50 - window.innerHeight / 100,
          }}
          transition={{ duration: 2, ease: "linear" }}
        />
      ))}
      
      <div className="absolute inset-0 flex items-center justify-center">
        <motion.div 
          className="absolute w-96 h-96 bg-black rounded-full shadow-lg"
          animate={{
            x: mousePosition.x - window.innerWidth / 2,
            y: mousePosition.y - window.innerHeight / 2,
            boxShadow: isMoving
              ? "0 0 60px 20px rgba(255,69,0,0.5)"
              : "0 0 100px 40px rgba(255,69,0,0.8)",
            scale: isMoving ? 1.02 : 1,
          }}
          transition={{ duration: 0.3, ease: "easeOut" }}
        />
      </div>
      
      <div className="relative z-10 text-center">
        <h1 className="text-4xl font-bold tracking-widest">STUDIO KÃ–Z</h1>
        <p className="mt-4 text-gray-400">architecture + urbanism</p>
      </div>
      
      <nav className="absolute right-10 top-1/2 transform -translate-y-1/2 text-sm text-gray-400 space-y-3">
        <p className="hover:text-white cursor-pointer">&gt; work</p>
        <p className="hover:text-white cursor-pointer">about</p>
        <p className="hover:text-white cursor-pointer">manifesto</p>
        <p className="hover:text-white cursor-pointer">research</p>
      </nav>
      
      <div className="absolute left-10 bottom-20 text-gray-400 max-w-xs text-sm">
        <p className="font-mono text-white">&gt; news</p>
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
      </div>
    </div>
  );
}
